package commands

import (
	"runtime"

	jc "github.com/juju/testing/checkers"
	gc "gopkg.in/check.v1"

	coretesting "github.com/juju/juju/testing"
)

var _ = gc.Suite(&DebugHooksSuite{})

type DebugHooksSuite struct {
	SSHCommonSuite
}

var debugHooksTests = []struct {
	info       string
	args       []string
	dialWith   dialerFunc
	forceAPIv1 bool
	error      string
	expected   *argsSpec
}{{
	info:       "literal script (api v1: unit name w/o hook or proxy)",
	args:       []string{"mysql/0"},
	dialWith:   dialerFuncFor("0.private", "0.public"),
	forceAPIv1: true,
	expected: &argsSpec{
		hostKeyChecking: "yes",
		knownHosts:      "0",
		enablePty:       true,
		args:            "ubuntu@0.public sudo /bin/bash -c 'F=$(mktemp); echo  | base64 -d > $F; . $F'",
	},
}, {
	info:       "unit name without hook (api v1)",
	args:       []string{"mysql/0"},
	dialWith:   dialerFuncFor("0.private", "0.public"),
	forceAPIv1: true,
	expected: &argsSpec{
		hostKeyChecking: "yes",
		knownHosts:      "0",
		enablePty:       true,
		argsMatch:       `ubuntu@0\.public sudo /bin/bash .+`,
	},
}, {
	info:       "unit name without hook (api v2)",
	args:       []string{"mysql/0"},
	dialWith:   dialerFuncFor("0.private", "0.public", "0.1.2.3"), // set by setAddresses() and setLinkLayerDevicesAddresses()
	forceAPIv1: false,
	expected: &argsSpec{
		hostKeyChecking: "yes",
		knownHosts:      "0",
		enablePty:       true,
		argsMatch:       `ubuntu@0\.(private|public|1\.2\.3) sudo .+`, // can be any of the 3
	},
}, {
	info:       "proxy (api v1)",
	args:       []string{"--proxy=true", "mysql/0"},
	dialWith:   dialerFuncFor("0.private", "0.public"),
	forceAPIv1: true,
	expected: &argsSpec{
		hostKeyChecking: "yes",
		knownHosts:      "0",
		enablePty:       true,
		withProxy:       true,
		argsMatch:       `ubuntu@0\.private sudo /bin/bash .+`,
	},
}, {
	info:       "proxy (api v2)",
	args:       []string{"--proxy=true", "mysql/0"},
	dialWith:   dialerFuncFor("0.private", "0.public", "0.1.2.3"), // set by setAddresses() and setLinkLayerDevicesAddresses()
	forceAPIv1: false,
	expected: &argsSpec{
		hostKeyChecking: "yes",
		knownHosts:      "0",
		enablePty:       true,
		withProxy:       true,
		argsMatch:       `ubuntu@0\.(private|public|1\.2\.3) sudo .+`, // can be any of the 3
	},
}, {
	info:     `"*" is a valid hook name: it means hook everything`,
	args:     []string{"mysql/0", "*"},
	expected: nil,
}, {
	info:     `"*" mixed with named hooks is equivalent to "*"`,
	args:     []string{"mysql/0", "*", "relation-get"},
	expected: nil,
}, {
	info:     `multiple named hooks may be specified`,
	args:     []string{"mysql/0", "start", "stop"},
	expected: nil,
}, {
	info:     `relation hooks have the relation name prefixed`,
	args:     []string{"mysql/0", "juju-info-relation-joined"},
	expected: nil,
}, {
	info:  `invalid unit syntax`,
	args:  []string{"mysql"},
	error: `"mysql" is not a valid unit name`,
}, {
	info:  `invalid unit`,
	args:  []string{"nonexistent/123"},
	error: `unit "nonexistent/123" not found`,
}, {
	info:  `invalid hook`,
	args:  []string{"mysql/0", "invalid-hook"},
	error: `unit "mysql/0" does not contain hook "invalid-hook"`,
}, {
	info:  `no args at all`,
	args:  nil,
	error: `no unit name specified`,
}}

func (s *DebugHooksSuite) TestDebugHooksCommand(c *gc.C) {
	//TODO(bogdanteleaga): Fix once debughooks are supported on windows
	if runtime.GOOS == "windows" {
		c.Skip("bug 1403084: Skipping on windows for now")
	}

	s.setupModel(c)

	for i, t := range debugHooksTests {
		c.Logf("test %d: %s\n\t%s\n", i, t.info, t.args)

		s.setHostDialerFunc(t.dialWith)
		s.setForceAPIv1(t.forceAPIv1)

		ctx, err := coretesting.RunCommand(c, newDebugHooksCommand(s.hostDialer), t.args...)
		if t.error != "" {
			c.Check(err, gc.ErrorMatches, t.error)
		} else {
			c.Check(err, jc.ErrorIsNil)
			if t.expected != nil {
				t.expected.check(c, coretesting.Stdout(ctx))
			}
		}
	}
}
